import '../styles/main.scss'

var arr = [];
var cardCounter = 0;

window.onload = function() {
  init();
};

function init() {
  var myTask = document.getElementById('mytask');
  myTask.addEventListener('click', function() {
    tabChange(myTask);
    var original = document.querySelector('.card');
    original.classList.remove('display');

    var doingCards = document.querySelectorAll('.card-list');
    for (let i = 0; i < doingCards.length; i++) {
      doingCards[i].classList.remove('display');
    }

  });

  var inProgress = document.getElementById('inprogress');
  inProgress.addEventListener('click', function() {
    tabChange(inProgress);
    var original = document.querySelector('.card');
    original.classList.add('display');

    var doingCards = document.querySelectorAll('.card-list.doing-card');
    console.log('doing-cards', doingCards);
    for (let i = 0; i < doingCards.length; i++) {
      doingCards[i].classList.remove('display');
    }

    var doneCards = document.querySelectorAll('.card-list.done-card');
    for (let i = 0; i < doneCards.length; i++) {
      doneCards[i].classList.add('display');
    }
  });

  var tabCompleted = document.getElementById('completed');
  tabCompleted.addEventListener('click', function() {
    tabChange(tabCompleted);
    var original = document.querySelector('.card');
    original.classList.add('display');

    var doingCards = document.querySelectorAll('.card-list.doing-card');
    console.log('doing-cards', doingCards);
    for (let i = 0; i < doingCards.length; i++) {
      doingCards[i].classList.add('display');
    }

    var doneCards = document.querySelectorAll('.card-list.done-card');
    for (let i = 0; i < doneCards.length; i++) {
      doneCards[i].classList.remove('display');
    }
  });

  var addTaskInput = document.querySelector('.text');
  addTaskInput.addEventListener('click', function() {
    var addTaskDetail = document.querySelector('.card .card-add-wrapper');
    addTaskDetail.classList.remove('display');

    var addBtns = document.querySelectorAll('.btns button');
    for (let i = 0; i < addBtns.length; i++) {
      addBtns[i].classList.remove('display');
    }
  });

  var cancelButtonClose = document.querySelector('button.cancel');
  cancelButtonClose.addEventListener('focus', function() {
    var addTaskDetail = document.querySelector('.card .card-add-wrapper');
    addTaskDetail.classList.add('display');

    var taskBtns = document.querySelectorAll('.btns button');
    for (let i = 0; i < taskBtns.length; i++) {
      taskBtns[i].classList.add('display');
    }
  });

  var addTaskButton = document.querySelector('#addtask');
  addTaskButton.addEventListener('click', function() {
    addRow();
  });


}


function addRow() {
  var todolistTitle = document.getElementById('input-title').value;
  var todolistDate = document.getElementById('input-date').value;
  console.log(todolistDate);
  var todolistComment = document.getElementById('input-comment').value;
  let obj = {
    title: todolistTitle,
    deadline: todolistDate,
    file: '',
    comment: todolistComment
  };
  arr.push(obj); // save to array
  ///
  const newInput = document.createElement('div');

  cardCounter++;
  let htmlString = `
  <div class="card-list doing-card" id="card-no-${cardCounter}">
    <div class="top">
      <div class="title">
        <label class="checkbox-label">
          <input type="checkbox" value="${cardCounter}">
          <div class="checkbox-box"></div>
        </label>
        <h1>${obj.title}</h1>
      </div>
      <div class="title-btns">
        <div class="tag doing">InProgress</div>
        <div class="tag done">DONE</div>
        <div class="img heart"></div>
        <div class="img edit"></div>
      </div>
    </div>
    <div class="description">
      <div class="img date"></div>
      <span>${obj.deadline}</span>
      <div class="img file"></div>
      <span></span>
      <div class="img comment"></div>
      <span></span>
    </div>
  </div>`;
  newInput.innerHTML = htmlString;
  var newCard = document.getElementById('allcards');
  newCard.appendChild(newInput);

  // only search for things inside this element
  newInput.querySelector('.checkbox-label input').addEventListener('change', checkboxChecked)
}

function tabChange(element) {
  var original = document.querySelector('.tabs .selected');
  original.classList.remove('selected');
  element.classList.add('selected');
}

function checkboxChecked(event) {
  // console.log(event.target.checked)
  const cardNumber = event.target.value;
  const card = document.querySelector('#card-no-' + cardNumber);
  if (event.target.checked) {
    // do something for checking the card
    card.classList.add('done-card')
    card.classList.remove('doing-card')
  } else {
    // do something for un-checking the card
    card.classList.add('doing-card')
    card.classList.remove('done-card')
  }
}
